* {
  margin: 0;
  padding: 0;
  cursor: none; }

/* to remove the top and left whitespace */
html, body {
  width: 100%;
  height: 100%;
  overflow: hidden; }

/* just to be sure these are full screen*/
canvas {
  display: block; }

/* To remove the scrollbars */
@font-face {
  font-family: phosphatesolid;
  src: url("ef5c720acd1fb9b4888e06487fd84bd5.woff2") format("woff2"), url("8bd91f23eb28f9c0bf523abc5fc7823e.woff") format("woff");
  font-weight: normal;
  font-style: normal; }

/*  MAIN CODE */
body {
  background: #f2f2f2; }

#logo {
  position: absolute;
  top: 50%;
  left: 50%;
  margin-right: -50%;
  perspective: 40px;
  transform: translate(-50%, -50%);
  transition: transform 0.5s;
  transform-style: preserve-3d; }

.firstName, .lastName {
  display: block;
  text-align: center;
  font-family: 'phosphatesolid', Tahoma, Geneva, Verdana, sans-serif;
  color: #fff;
  font-size: 8em;
  padding: 0;
  margin: 0;
  line-height: 91px;
  font-weight: normal;
  perspective: 40px;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  text-shadow: 0px 15px 5px rgba(0, 0, 0, 0.1), 10px 20px 5px rgba(0, 0, 0, 0.05), -10px 20px 5px rgba(0, 0, 0, 0.05); }

.firstName {
  transform: translateZ(30px) scale(0.64); }

.lastName {
  transform: translateZ(-30px) scale(6);
  /* position: relative; */
  /* top: -20px; */
  color: #851e3e; }

/* video */
.videoContainer {
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center; }

iframe {
  /* optional */
  width: 100%;
  height: 100%;
  pointer-events: none; }

.cursor {
  position: absolute;
  display: block;
  width: 50px;
  height: 50px;
  top: 0;
  left: 0;
  border: 1px solid red;
  border-radius: 50px;
  pointer-events: none; }
